.csv_data_uri <- function(df) {
  enc_csv <- base64enc::base64encode(
    charToRaw(
      paste(
        capture.output(
          write.csv(df)),
          collapse = "\n")
    )
  )
  glue::glue("data:text/csv;base64,{enc_csv}")
}
