.csv_data_uri <- function(df) {
  enc_csv <- base64enc::base64encode(
    charToRaw(
      paste(
        capture.output(
          write.csv(df)),
          collapse = "\n")
    )
  )
  glue::glue("data:text/csv;base64,{enc_csv}")
}
.save_restore_progress <- function(assignment_name, df = .webr_progress) {
  now <- strftime(Sys.time(), "%Y%m%d-%H%M%S")
  htmltools::div(
    htmltools::tags$a(
      download = glue::glue('adm-{.assignment_ids[assignment_name]}-{now}.csv'),
      href = .csv_data_uri(df),
      htmltools::tags$button(
          #onClick = "this.classList.add('clicked');",
          class = "btn progress-submit",
          "Save progress",
          .noWS = "outside"
      )
    ),
    htmltools::tags$label(
      `for` = "restore-file-input",
      type = "button",
      id = "restore-progress-button",
      class = "btn progress-submit btn-quarto",
      onClick = "restore_listener();",
      "Restore progress"
    ),
    htmltools::tags$input(
      type = "file",
      id = "restore-file-input",
      style = "display: none",
      accept = ".csv"
    )
  )
}
.steps_to_submit <- function(assignment_name, df = .webr_progress) {
